package com.chaipoint.thirdparty.helper.shadowfax;

public class RiderDetails {
	private String rider_name;
	private RiderLocation rider_location;
	private String rider_phone;

	public String getRider_name() {
		return rider_name;
	}

	public void setRider_name(String rider_name) {
		this.rider_name = rider_name;
	}

	public RiderLocation getRider_location() {
		return rider_location;
	}

	public void setRider_location(RiderLocation rider_location) {
		this.rider_location = rider_location;
	}

	public String getRider_phone() {
		return rider_phone;
	}

	public void setRider_phone(String rider_phone) {
		this.rider_phone = rider_phone;
	}

}
